<script>
  function selectProducts() {

    var category = document.getElementById('categorySelect').value;
    var subCategory = document.getElementById('subCategorySelect').value;

    if (category != '0') {
      if (subCategory != '0') {
        window.location = "search.php?category=" + category + "&subCategory=" + subCategory;
      } else {
        window.location = "search.php?category=" + category;
      }
    } else {
      window.location = "search.php";
    }

  }

  function closeNav() {
    document.getElementById('modal--content').classList.remove("modal--show");
    document.getElementById('modal--content').classList.add("modal--hide");
    setTimeout(function() {
      document.getElementById('modal').style.display = "none";
    }, 700);
  }

  function openNav() {
    document.getElementById('modal2').style.display = 'none';
    document.getElementById('modal--content').classList.remove("modal--hide");
    document.getElementById('modal--content').classList.add("modal--show");
    document.getElementById('modal').style.display = "block";
  }

  function loadSubCategory() {
    var id = document.getElementById('categorySelect').value;

    var form = new FormData();
    form.append("id", id);

    var r = new XMLHttpRequest();

    r.onreadystatechange = function() {
      if (r.readyState == 4) {
        var text = r.responseText;
        document.getElementById('subCategoryContent').innerHTML = text;
      }
    }

    r.open("POST", "subcategoryLoad.php", true);
    r.send(form);
  }
</script>